import * as PacketConstants from './packets/Packets';
import * as ErrorConstants from './errors/Errors';

/**
 * The Lucid Protocol version that the server and client are running.
 * @type {String}
 */
export const ProtocolVersion = 'alpha-experimental-1';

/**
 * Describes the state of the Server.
 * @type {Object}
 * @property {Number} AVAILABLE Shows that the server is ready/available.
 * @property {Number} UNAVAILABLE Shows that the server is unavailable and is not attempting to start/restart.
 * @property {Number} RESTARTING Shows that the server is unavailable but is trying to restart.
 * @property {Number} STARTING Shows that the server is starting up for the first time.
 */
export const ServerStatus = {
  AVAILABLE: 0,
  UNAVAILABLE: 1,
  RESTARTING: 2,
  STARTING: 3,
};

/**
 * Describes the state of a Client.
 * @type {Object}
 * @property {Number} ACTIVE Shows that the Client is active - it is connected.
 * @property {Number} INACTIVE Shows that the Client is inactive - it is disconnected, but has the chance
 * to renew its session. Clients may be inactive when their connection has dropped.
 * @property {Number} REMOVED Shows that the Client is disconnected and they are not permitted to return to their
 * session.
 * @property {Number} UNAUTHORIZED Shows that the Client is unauthorized. Only applies to UnknownClient.
 */
export const ClientStatus = {
  ACTIVE: 0,
  INACTIVE: 1,
  REMOVED: 2,
  UNAUTHORIZED: 3,
};

/**
 * Default options for a Server's setup.
 * @type {ServerOptions}
 */
export const ServerDefaultOptions = {
  wss: {
    allowedAuthTime: 1000,
  },
  express: {},
  mount: '/',
  maxClients: 25,
};

/*

 PACKETS

 */
/**
 * Object containing Packet Types
 * @type {Object}
 */
export const PacketTypes = PacketConstants.PacketType;

/**
 * Object containing Disconnect Packet Types
 * @type {Object}
 */
export const DisconnectTypes = PacketConstants.DisconnectPacketType;

/*

 ERRORS

 */
/**
 * Object containing Error Types
 * @type {Object}
 */
export const Errors = ErrorConstants.Errors;

/**
 * Thrown when the client send an incorrect message i.e. bad compression or bad json
 * @type {Error}
 */
const INVALID_MESSAGE_FROM_CLIENT = new Error(
  'The received message was of an invalid format i.e. bad compression or json');

/**
 * Object containing custom errors
 * @type {Object}
 * @ignore
 */
export const Errors = {
  INVALID_MESSAGE_FROM_CLIENT,
};

/**
 * Describes a Client requesting a new session.
 * @type {Number}
 */
const CLIENT_NEW_SESSION_REQUEST = 1;

/**
 * Describes a Client requesting to return to an existing session.
 * @type {Number}
 */
const CLIENT_EXISTING_SESSION_REQUEST = 2;

/**
 * Describes when the server is going to disconnect a client.
 * @type {Number}
 */
const SERVER_DISCONNECTING_CLIENT = 3;

/**
 * Describes when a Server accepts a new/existing session request.
 * @type {Number}
 */
const SERVER_ACCEPT_SESSION_REQUEST = 4;

/**
 * @ignore
 */
export const PacketType = {
  CLIENT_NEW_SESSION_REQUEST,
  CLIENT_EXISTING_SESSION_REQUEST,
  SERVER_DISCONNECTING_CLIENT,
  SERVER_ACCEPT_SESSION_REQUEST,
};

/*
  DISCONNECT TYPES
*/

/**
 * DisconnectType - Describes when the Server is disconnecting/refusing the client as it is running at maximum capacity.
 * @type {Number}
 */
const SERVER_AT_MAXIMUM_CAPACITY = 1;

/**
 * DisconnectType - Describes when a Client opens a connection to the server, but takes too long to send an
 * authentication packet.
 * @type {Number}
 */
const TOOK_TOO_LONG_TO_AUTH = 2;

/**
 * @ignore
 */
export const DisconnectPacketType = {
  SERVER_AT_MAXIMUM_CAPACITY,
  TOOK_TOO_LONG_TO_AUTH,
};

'use strict';

/* server */
import LucidServer from './server/Server';
import LucidExpressServer from './server/ExpressServer';

/* utils */
import * as Util from './util/Util';
import * as Constants from './constants/Constants';

export {
  LucidServer,
  LucidExpressServer,
  Constants,
  Util
};

'use strict';

/**
* Object containing options for ExpressServer
* @typedef {(object)} ExpressServerOptions
*/

import http from 'http';
import express from 'express';
import cors from 'cors';
import bodyParser from 'body-parser';

/**
 * The LucidExpressServer handles HTTP REST requests for the Lucid Server, e.g. server metadata.
 */
class LucidExpressServer{

  /**
   * Creates a new ExpressServer. Developers shouldn't instantiate this class themselves, it is done by LucidServer.
   * @param {LucidServer} lucidServer The Lucid Server that the ExpressServer is serving under.
   */
  constructor(lucidServer) {
    /**
     * Lucid Server that the ExpressServer is serving under.
     * @type {LucidServer}
     */
    this.lucidServer = lucidServer;
    /**
     * Options for the ExpressServer, obtained from the owning Server's options.
     * @type {ExpressServerOptions}
     */
    this.options = lucidServer.options.express;

    /**
     * The express application that the server is using.
     * @type {express.Application}
     */
    this.app = express();

    /**
     * Express Router for managing core Lucid requests.
     * @type {express.Router}
     */
    this.metaRouter = express.Router();

    /**
     * Express Router for managing custom requests.
     * @type {express.Router}
     */
    this.customRouter = express.Router();

    // allow Cross-Origin Resource Sharing (external access to server)
    this.app.use(cors());

    // the mount point that the express server should build upon
    let baseMount = this.lucidServer.options.mount;

    this.metaRouter.get('/info', (req, res) => {
      res.json({});
    });

    // register handlers
    this.app.use(baseMount + 'meta', this.metaRouter);
    this.app.use(baseMount + 'custom', this.customRouter);

    /**
     * The HTTP server the express application is running on.
     * @type {http.Server}
     */
    this.httpServer = null;
  }

  /**
   * Attaches a HTTP server to the Express Server
   * @param {http.Server} httpServer the server to attach
   * @private
   */
  attach(httpServer) {
    let app = this.app;
    this.httpServer = httpServer;

    /*
      Get the HTTP server to let express handle requests
     */
    httpServer.on('request', app);
  }

};

export default LucidExpressServer;

'use strict';

/**
* Object containing the options for the Server
* @typedef {(object)} ServerOptions
* @property {ExpressServerOptions} [express] Options for the Express (REST API) Server.
* @property {WebSocketServerOptions} [wss] Options for the WebSocket Server.
* @property {String} [mount=/] Path to mount the LucidServer on, defaults to `/` (root)
* @property {Number} [maxClients=25] Maximum amount of clients the server should handle
*/

import EventEmitter from 'events';
import ExpressServer from './ExpressServer';
import WebSocketServer from './WebSocketServer';
import * as Util from '../util/Util';
import * as Constants from '../constants/Constants';

/**
* Manages the Express (HTTP), WebSocket Server and connections
*/
class LucidServer extends EventEmitter{

  /**
  * Creates a new LucidServer class.
  * @param  {ServerOptions} [options] Options to pass to the object.
  */
  constructor(options) {
    super();
    /**
    * The options passed to the Server upon instantiation.
    * @type {ServerOptions}
    */
    this.options = Util.Options(Constants.ServerDefaultOptions, options);

    /**
     * The status of the server
     * @type {ServerStatus}
     */
    this.status = Constants.ServerStatus.UNAVAILABLE;

    /**
     * The Express Server of the Client
     * @type {LucidExpressServer}
     */
    this.express = new ExpressServer(this);

    /**
     * The WebSocket Server of the Client
     * @type {LucidWebSocketServer}
     */
    this.wss = new WebSocketServer(this);
  }

  /**
   * Attaches a HTTP Server to the Express and WebSocket Servers, essentially starting up the LucidServer.
   * @param  {http.Server} httpServer The HTTP Server to attach
   */
  attach(httpServer) {
    this.express.attach(httpServer);
    this.wss.attach(httpServer);
  }

};

export default LucidServer;

'use strict';

import crypto from 'crypto';
import AuthorizedClient from '../structures/AuthorizedClient';
import UnknownClient from '../structures/UnknownClient';
import * as Constants from '../constants/Constants';

/**
* Object containing options for LucidWebSocketServer
* @typedef {(object)} WebSocketServerOptions
* @property {Number} [hearbeat=45000] Interval of heartbeats in milliseconds. Set to -1 to disable heartbeats.
* Heartbeats are used to verify whether connections are active.
* @property {Number} [allowedAuthTime=5000] Maximum time in milliseconds that a client has to request authentication
* before it is disconnected.
*/

import {Server} from 'ws';

/**
 * The WebSocketServer handles WebSocket connections and sessions
 */
class LucidWebSocketServer{
  /**
   * Creates a new WebSocketServer. Developers shouldn't instantiate this class themselves, it is done by LucidServer.
   * @param  {LucidServer} lucidServer The LucidServer that the WebSocketServer is running under.
   */
  constructor(lucidServer) {

    /**
     * Lucid Server that the ExpressServer is serving under.
     * @type {LucidServer}
     */
    this.lucidServer = lucidServer;
    /**
     * Options for the WebSocketServer, obtained from the owning Server's options.
     * @type {WebSocketServerOptions}
     */
    this.options = lucidServer.options.wss;

    /**
     * The WebSocket Server that the LucidWebSocketServer class wraps around.
     * @type {ws.Server}
     */
    this.wss = null;

    /**
     * An Array of Authorized Clients.
     * @type {Array<AuthorizedClient>}
     */
    this.connections = [];

  }

  /**
   * Attaches a HTTP Server, essentially starting the WebSocket Server.
   * @param  {http.Server} httpServer The HTTP Server to attach.
   * @private
   */
  attach(httpServer) {

    // the mount point that the WS server should build upon
    let baseMount = this.lucidServer.options.mount;

    this.wss = new Server({ server: httpServer, path: baseMount + 'ws', });

    this.attachHandlers();

  }

  /**
   * Describes whether the server has the capacity to take any new connections.
   * @return {Boolean} Returns true if there is capacity for a new connection.
   */
  get canAllowNewConnections() {
    return this.connections.length < this.lucidServer.options.maxClients;
  }

  /**
   * Attach WSS Handlers
   * @private
   */
  attachHandlers() {
    this.wss.on('connection', (socket) => this.EventConnection(socket));
    this.wss.on('error', (error) => this.EventError(error));
    this.wss.on('headers', (headers) => this.EventHeaders(headers));
  }

  /**
   * Handles WebSocketServer 'connection' events
   * @param {ws.WebSocket} socket
   * @private
   */
  EventConnection(socket) {
    let unknownClient = new UnknownClient(socket, this);
  }

  /**
   * Handles WebSocketServer 'error' events
   * @param {Error} error
   * @private
   */
  EventError(error) {

  }

  /**
   * Handles WebSocketServer 'headers' events
   * @param {Object} headers
   * @private
   */
  EventHeaders(headers) {

  }

  /**
   * Authorizes a previously "unknown" client with a new session.
   * @param {UnknownClient} unknownClient Client to authorize
   */
  AuthorizeNewSession(unknownClient) {
    let uuid = crypto.randomBytes(16).toString('hex');
    let token = crypto.randomBytes(64).toString('base64');

    unknownClient.breakDown();

    let authorizedClient = new AuthorizedClient(
      unknownClient.socket, // socket
      this, // wss
      uuid, // uuid
      token // token
    );

    let packetData = { uuid, token };
    let heartbeat = this.lucidServer.options.wss.hearbeat;

    if (this.lucidServer.options.hearbeat > -1) {
      packetData.heartbeatInterval = heartbeat;
    }

    authorizedClient.sendInternal(Constants.PacketTypes.SERVER_ACCEPT_SESSION_REQUEST, packetData);

    this.connections.push(authorizedClient);
  }
};

export default LucidWebSocketServer;

'use strict';

import {PacketTypes, DisconnectTypes, ClientStatus} from '../constants/Constants';
import Client from './Client';

/**
 * Represents a WebSocket Client that has authorised/authorized themselves.
 */
class AuthorizedClient extends Client{

  /**
   * Creates a new AuthorizedClient. Developers shouldn't instantiate AuthorizedClients,
   * the WebSocketServer does it automatically.
   * @param {ws.WebSocket} socket the WebSocket connection
   * @param {LucidWebSocketServer} wss the WSS the Client is connected to.
   * @param {String} uuid The UUID of the Client
   * @param {String} token The private token of the Client
   */
  constructor(socket, wss, uuid, token) {
    super(socket, wss);

    /**
     * ACTIVE by default because when an AuthorizedClient is first created, it means a WebSocket connection
     * is active
     * @ignore
     */
    this.status = ClientStatus.ACTIVE;

    /**
     * @ignore
     */
    this.authorized = true;

    /**
     * The UUID of the Client
     * @type {String}
     */
    this.uuid = uuid;

    /**
     * The private token of the Client
     * @type {String}
     */
    this.token = token;
  }
}

export default AuthorizedClient;

'use strict';

import zlib from 'zlib';
import * as Constants from '../constants/Constants';

/**
 * Represents a WebSocket Client.
 */
class Client{

  /**
   * Creates a new Client. Developers shouldn't instantiate Client,
   * the WebSocketServer does it automatically.
   * @param {ws.WebSocket} socket the WebSocket connection
   * @param {LucidWebSocketServer} wss the WSS the Client is connected to.
   */
  constructor(socket, wss) {
    /**
     * The LucidWebSocketServer that the Client is connected to.
     * @type {LucidWebSocketServer}
     */
    this.wss = wss;

    /**
     * The WebSocket connection of the Client.
     * @type {ws.WebSocket}
     */
    this.socket = socket;

    /**
     * Represents whether the Client is authorized or not.
     * @type {Boolean}
     */
    this.authorized = false;

    /**
     * An Array of Intervals. They are stored so they can be cleared if required.
     * @type {Array<Interval>}
     */
    this.intervals = [];

    /**
     * An Array of Timeouts. They are stored so they can be cleared if required.
     * @type {Array<Timeout>}
     */
    this.timeouts = [];

    /**
     * The status of the Client.
     * @type {ClientStatus}
     */
    this.status = null;

    this.attachHandlers();
  }

  /**
   * Attaches event handlers to the WebSocket
   * @private
   */
  attachHandlers() {
    let socket = this.socket;

    socket.on('message', (data, flags) => this.PreEventMessage(data, flags));
    socket.on('close', (code, message) => this.EventClose(code, message));
    socket.on('error', (error) => this.EventError(error));
  }

  /**
   * Removes all message, close and error event listeners.
   */
  detachHandlers() {
    let socket = this.socket;

    socket.removeAllListeners('message');
    socket.removeAllListeners('close');
    socket.removeAllListeners('error');
  }

  /**
   * Handles the WebSocket 'message' event and normalizes data
   * @param {String|Buffer} data The received data.
   * @param {Object} flags Object containing 'binary' member.
   */
  PreEventMessage(data, flags) {

    // turn data into string

    if (flags.binary) {
      try {
        // try to inflate the data
        data = zlib.inflateSync(data).toString();
      } catch (e) {
        return this.EventError(Constants.Errors.INVALID_MESSAGE_FROM_CLIENT);
      }
    }

    // parse json data
    try {
      data = JSON.parse(data);
    } catch (e) {
      return this.EventError(Constants.Errors.INVALID_MESSAGE_FROM_CLIENT);
    }

    if (!data.t) {
      return this.EventError(Constants.Errors.INVALID_MESSAGE_FROM_CLIENT);
    }

    // data is now a valid packet

    this.EventMessage(data);
  }

  /**
   * Handles normalized data from 'PreEventMessage'
   * @param {Object} packet The received packet
   * @abstract
   */
  EventMessage(packet) {

  }

  /**
   * Handles the WebSocket 'close' event
   * @param {Number} code The close code
   * @param {String} message The given close message
   */
  EventClose(code, message) {

  }

  /**
   * Handles the WebSocket 'error' event
   * @param {Error} error An error
   */
  EventError(error) {

  }

  /**
   * Sends a custom packet to the Client
   * @param {String} type packet type
   * @param {Object} data packet data
   */
  send(type, data) {
    type = 'custom_' + type;
    return this.sendInternal(type, data);
  }

  /**
   * Sends an unmodified packet to the Client
   * @param {String} type packet type
   * @param {Object} data packet data
   */
  sendInternal(type, data) {
    let t = type;
    let d = data || {};
    this.socket.send(JSON.stringify({ t, d }));
  }

  /**
   * Kills connection to the client
   * @param {Number} reason A numeric reason, see DisconnectTypes.
   * @param  {Boolean} [returnAllowed=true] States whether the Client is allowed to return to their session
   * after being disconnected.
   * @param {Object} [extra] Extra information about the disconnect, useful for custom disconnect reasons.
   */
  killConnection(reason, returnAllowed=true, extra) {
    let data = {
      r: reason,
    };

    if (extra) {
      data.e = extra;
    }

    if (this.authorized) {
      data.returnAllowed = Boolean(returnAllowed);
    }

    this.sendInternal(Constants.PacketTypes.SERVER_DISCONNECTING_CLIENT, data);

    if (data.returnAllowed) {
      this.cleanWebSocket();
    } else {
      this.cleanWebSocket();
      this.removeReferences();
    }
  }

  /**
   * Terminates the WebSocket and cleans all WebSocket related information, but doesn't end the session.
   * @ignore
   */
  cleanWebSocket() {
    this.socket.close();

    for (let interval of this.intervals) {
      clearInterval(interval);
    }

    for (let timeout of this.timeouts) {
      clearTimeout(timeout);
    }
  }

  /**
   * Removes references to the Client internally
   * @ignore
   */
  removeReferences() {
    let index = this.wss.connections.indexOf(this);
    if (index > -1) {
      this.wss.connections.splice(index, 1);
    }
  }
}

export default Client;

'use strict';

import Client from './Client';
import {PacketTypes, DisconnectTypes, ClientStatus} from '../constants/Constants';

/**
 * Represents a WebSocket Client that has connected, but has not authorised/authorized yet.
 */
class UnknownClient extends Client{

  /**
   * Creates a new UnknownClient. Developers shouldn't instantiate UnknownClient,
   * the WebSocketServer does it automatically.
   * @param {ws.WebSocket} socket the WebSocket connection
   * @param {LucidWebSocketServer} wss the WSS the Client is connected to.
   */
  constructor(socket, wss) {
    super(socket, wss);

    /**
     * @ignore
     */
    this.status = ClientStatus.UNAUTHORIZED;

    this._passedAuth = false;

    setTimeout(() => this.checkAuth(), this.wss.lucidServer.options.wss.allowedAuthTime);
  }

  /**
   * Checks whether or not the Client has been authenticated. If it hasn't, it is disconnected.
   */
  checkAuth() {
    if (!this._passedAuth) {
      return this.killConnection(DisconnectTypes.TOOK_TOO_LONG_TO_AUTH);
    }
  }

  /**
   * Handles packet events
   * @param {Object} packet the received packet
   */
  EventMessage(packet) {
    super.EventMessage(packet);
    switch (packet.t){
      case PacketTypes.CLIENT_NEW_SESSION_REQUEST:

        // takes no data
        if (this.wss.canAllowNewConnections) {
          // can take a new connection
          this._passedAuth = true;
          this.wss.AuthorizeNewSession(this);

        } else {
          // can't take any more connections
          return this.killConnection(DisconnectTypes.SERVER_AT_MAXIMUM_CAPACITY);
        }

        break;
      case PacketTypes.CLIENT_EXISTING_SESSION_REQUEST:
        break;
    }
  }

  /**
   * Breaks down the unknown client, removing listeners.
   */
  breakDown() {
    this.detachHandlers();
  }
}

export default UnknownClient;

